export default class Bricks {
  constructor(ctx, columnCount, rowCount, width, height, padding, offsetLeft, offsetTop) {
    this.ctx = ctx;
    this.columnCount = columnCount;
    this.rowCount = rowCount;
    this.width = width;
    this.height = height;
    this.padding = padding;
    this.offsetLeft = offsetLeft;
    this.offsetTop = offsetTop;
    this.bricks = [];
  }

  #defineBricksArray() {
    for(let column = 0; column < this.columnCount; column++) {
      this.bricks[column] = [];
      for(let row = 0; row < this.rowCount; row++) {
        this.bricks[column][row] = { x: 0, y: 0, status: 1 };
      }
    }
  }

  draw() {
    for(let c=0; c<this.columnCount; c++) {
      for(let r=0; r<this.rowCount; r++) {
        if(this.bricks[c][r].status === 1) {
          const brickX = (r * (this.width + this.padding)) + this.offsetLeft;
          const brickY = (c * (this.height + this.padding)) + this.offsetTop;
          this.bricks[c][r] = { x: brickX, y: brickY, status: 1 };
          this.ctx.beginPath();
          this.ctx.rect(brickX, brickY, this.width, this.height);
          this.ctx.fillStyle = "#0095DD";
          this.ctx.fill();
          this.ctx.closePath();
        }
      }
    }
  }
}
